";s:6:"filter";b:1;s:6:"visual";b:1;s:22:"so_sidebar_emulator_id";s:14:"text-190410000";s:11:"option_name";s:11:"widget_text";s:11:"panels_info";a:7:{s:5:"class";s:14:"WP_Widget_Text";s:3:"raw";b:0;s:4:"grid";i:0;s:4:"cell";i:0;s:2:"id";i:0;s:9:"widget_id";s:36:"a0568f72-c2fd-4739-9b76-ab97540c5206";s:5:"style";a:4:{s:10:"widget_css";s:56:"width: 1195px;
